import React, { useState } from "react";

// üîä Speech function with optional language support
const speak = (text, lang = "en-US") => {
  if (window.speechSynthesis) {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.lang = lang;
    speechSynthesis.cancel();
    speechSynthesis.speak(utterance);
  }
};

const Nav = () => {
  const [showLangMenu, setShowLangMenu] = useState(false);

  const handleHover = (text, lang = "en-US") => {
    speak(text, lang);
  };

  const languages = [
    { label: "English", code: "en-US" },
    { label: "Hindi", code: "hi-IN" },
    { label: "Spanish", code: "es-ES" },
    { label: "French", code: "fr-FR" },
    { label: "German", code: "de-DE" },
  ];

  return (
    <nav className="bg-black text-white px-6 py-3 flex justify-between items-center shadow-md relative">
      {/* Left Menu: Navigation Tabs */}
      <div className="flex space-x-6 text-sm font-semibold">
        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("For Individuals")}
          tabIndex="0"
          aria-label="For Individuals"
        >
          For Individuals
        </span>
        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("For Businesses")}
          tabIndex="0"
          aria-label="For Businesses"
        >
          For Businesses
        </span>
        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("For Universities")}
          tabIndex="0"
          aria-label="For Universities"
        >
          For Universities
        </span>
        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("For Governments")}
          tabIndex="0"
          aria-label="For Governments"
        >
          For Governments
        </span>
      </div>

      {/* Right Menu: Language / Notification / Profile */}
      <div className="flex items-center space-x-4 text-sm">
        {/* üåç Language Dropdown */}
        <div
          className="relative"
          onMouseEnter={() => setShowLangMenu(true)}
          onMouseLeave={() => setShowLangMenu(false)}
        >
          <span
            className="cursor-pointer hover:underline flex items-center"
            tabIndex="0"
            aria-label="Language selection"
          >
            Language üåç
          </span>
          {showLangMenu && (
            <ul className="absolute top-7 right-0 bg-white text-black shadow-lg rounded w-40 z-50">
              {languages.map((lang) => (
                <li
                  key={lang.code}
                  className="px-4 py-2 hover:bg-gray-100 cursor-pointer text-sm"
                  onMouseEnter={() => handleHover(lang.label, lang.code)}
                >
                  {lang.label}
                </li>
              ))}
            </ul>
          )}
        </div>

        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("Notifications")}
          tabIndex="0"
          aria-label="Notifications"
        >
          üîî
        </span>
        <span
          className="cursor-pointer hover:underline"
          onMouseEnter={() => handleHover("Save")}
          tabIndex="0"
          aria-label="Save"
        >
          üíæ Save
        </span>
        <div
          className="rounded-full bg-white text-black w-6 h-6 flex items-center justify-center text-xs font-bold cursor-pointer"
          onMouseEnter={() => handleHover("Profile")}
          tabIndex="0"
          aria-label="Profile"
        >
          S
        </div>
      </div>
    </nav>
  );
};

export default Nav;
