import React from "react";
import { Routes, Route, useParams } from "react-router-dom";
import AutoReader from "./components/AutoReader";
import VoiceCommand from "./components/VoiceCommand";
import KeyboardAnnouncer from "./components/KeyboardAnnouncer";
import Nav from "./components/Nav";
import CourseCard from "./components/CourseCard";
import { speak } from "./utils/speak";

import image1 from "./assets/Google_cybersecurity_theme.jpeg";
import image2 from "./assets/Business_Intelligence.jpeg";
import image3 from "./assets/Advanced_Data_Analytics.jpeg";
import image4 from "./assets/Project_Management.jpeg";

// Sample course data
const courseData = {
  "google-cybersecurity": {
    title: "Google Cybersecurity",
    description: "Protect organizations from cyber threats.",
    instructor: "Jane Doe",
    duration: "3 months",
    level: "Beginner",
    image: image1,
    about: [
      "Learn security principles and practices.",
      "Understand threat models and risk assessments.",
      "Perform basic forensic investigation.",
    ],
  },
  "google-business-intelligence": {
    title: "Google Business Intelligence",
    description: "Analyze and visualize business data.",
    instructor: "John Smith",
    duration: "4 months",
    level: "Intermediate",
    image: image2,
    about: [
      "Learn to visualize data using Looker Studio.",
      "Develop dashboards with BigQuery.",
      "Generate insights using SQL and reports.",
    ],
  },
  "google-advanced-data-analytics": {
    title: "Google Advanced Data Analytics",
    description: "Master predictive analytics and big data.",
    instructor: "Alice Johnson",
    duration: "5 months",
    level: "Advanced",
    image: image3,
    about: [
      "Explore big data technologies and analytics.",
      "Train and evaluate machine learning models.",
      "Develop advanced data visualizations.",
    ],
  },
  "google-project-management": {
    title: "Google Project Management",
    description: "Lead projects with agile and scrum.",
    instructor: "Emily Lee",
    duration: "3 months",
    level: "Beginner",
    image: image4,
    about: [
      "Understand project management frameworks.",
      "Use agile methodologies and scrum tools.",
      "Handle project risk and stakeholder communication.",
    ],
  },
};

// Detail page
const CourseDetail = () => {
  const { slug } = useParams();
  const course = courseData[slug];

  if (!course) return <div className="p-6">❌ Course not found</div>;

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-4 text-white">
      <h1 className="text-4xl font-bold" onMouseEnter={() => speak(course.title)}>
        {course.title}
      </h1>
      <img
        src={course.image}
        alt={course.title}
        className="rounded-lg w-full max-h-64 object-cover"
        onMouseEnter={() => speak("Course image")}
      />
      <p className="text-lg" onMouseEnter={() => speak(course.description)}>
        {course.description}
      </p>
      <div className="text-gray-900 space-y-1">
        <p onMouseEnter={() => speak(`Instructor: ${course.instructor}`)}>
          <strong>Instructor:</strong> {course.instructor}
        </p>
        <p onMouseEnter={() => speak(`Duration: ${course.duration}`)}>
          <strong>Duration:</strong> {course.duration}
        </p>
        <p onMouseEnter={() => speak(`Level: ${course.level}`)}>
          <strong>Level:</strong> {course.level}
        </p>
        <div onMouseEnter={() => speak("About the course")}>
          <strong>About:</strong>
          <ul className="list-disc ml-5 mt-1 space-y-1">
            {course.about.map((item, index) => (
              <li key={index} onMouseEnter={() => speak(item)}>
                {item}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

// Home page
const Home = () => (
  <main className="mt-6 space-y-4 min-h-screen text-black px-6 py-4">
    <div
      className="p-6 rounded shadow-md"
      style={{
        backgroundImage: 'url("/image.png")',
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <h1 className="text-3xl font-bold" onMouseEnter={() => speak("Welcome to ClickAble")}>
        Welcome to ClickAble
      </h1>
      <p onMouseEnter={() => speak("This is a voice-assisted, keyboard-navigable platform designed to help blind users access digital content.")}>
        This is a voice-assisted, keyboard-navigable platform designed to help blind users access digital content.
      </p>
    </div>

    <section className="bg-white/80 backdrop-blur rounded shadow-md mt-8 p-6 space-y-6 text-black">
      <div className="bg-blue-900 text-white text-center py-6 rounded">
        <h2 className="text-3xl font-bold" onMouseEnter={() => speak("New York Tech Google Learning Program")}>
          New York Tech Google Learning Program
        </h2>
        <p className="text-lg" onMouseEnter={() => speak("Start Getting Certified With Google")}>
          Start Getting Certified With Google!
        </p>
      </div>

      <div className="grid md:grid-cols-3 gap-6 text-center">
        <div onMouseEnter={() => speak("No experience necessary")}>
          <p className="font-bold">No experience necessary</p>
          <p className="text-sm text-gray-600">
            Learn job-ready skills even with no relevant experience or a post-secondary degree.
          </p>
        </div>
        <div onMouseEnter={() => speak("Learn at your own pace")}>
          <p className="font-bold">Learn at your own pace</p>
          <p className="text-sm text-gray-600">
            Complete the online program on your own time and set your own schedule.
          </p>
        </div>
        <div onMouseEnter={() => speak("Stand out to employers")}>
          <p className="font-bold">Stand out to employers</p>
          <p className="text-sm text-gray-600">
            Make your resume competitive with a credential from Google.
          </p>
        </div>
      </div>

      <div
        className="bg-blue-100 p-6 rounded text-center"
        onMouseEnter={() =>
          speak("TOP INSTRUCTOR. 75 percent of Google Career Certificate Graduates report an improvement in their career trajectory within 6 months of completion.")
        }
      >
        <p className="text-sm text-blue-900">
          <span className="font-semibold bg-blue-800 text-white px-2 py-1 rounded mr-2">
            TOP INSTRUCTOR
          </span>
          <strong>75% of Google Career Certificate Graduates</strong> report an improvement in their career trajectory within 6 months of completion.
        </p>
      </div>
    </section>

    <section className="mt-10 space-y-4">
      <div className="text-center">
        <h2 className="text-2xl font-bold" onMouseEnter={() => speak("Recommended by New York Institute of Technology")}>
          Recommended by New York Institute of Technology NYIT
        </h2>
        <p className="text-sm text-gray-600" onMouseEnter={() => speak("These courses cover skills your organization is focused on improving.")}>
          These courses cover skills your organization is focused on improving.
        </p>
      </div>

      <div className="flex flex-wrap gap-4 justify-center">
        {Object.entries(courseData).map(([slug, course]) => (
          <CourseCard
            key={slug}
            title={course.title}
            subtitle="Professional Certificate"
            image={course.image}
            badge={course.level === "Beginner" ? "New AI skills" : ""}
            slug={slug}
          />
        ))}
      </div>
    </section>
  </main>
);

// App component
const App = () => {
  const welcomeText = `
    Welcome to AccessMate. This accessibility toolkit was created to help completely blind users.
    You can speak commands like 'read page' or 'help' to interact with the system.
    Use Tab to navigate. Each element will be announced for your convenience.
  `;

  return (
    <div className="p-4 font-sans">
      <AutoReader text={welcomeText} />
      <VoiceCommand />
      <KeyboardAnnouncer />
      <Nav />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/courses/:slug" element={<CourseDetail />} />
      </Routes>
    </div>
  );
};

export default App;
