import React from "react";
import AutoReader from "./components/AutoReader";
import VoiceCommand from "./components/VoiceCommand";
import KeyboardAnnouncer from "./components/KeyboardAnnouncer";
import Nav from "./components/Nav"; // Optional

function App() {
  const welcomeText = `
    Welcome to AccessMate. This accessibility toolkit was created to help completely blind users.
    You can speak commands like 'read page' or 'help' to interact with the system.
    Use Tab to navigate. Each element will be announced for your convenience.
  `;

  return (
    <div className="p-4 font-sans">
      <AutoReader text={welcomeText} />
      <VoiceCommand />
      <KeyboardAnnouncer />
      <Nav />

      <main className="mt-6 space-y-4">
        <h1 tabIndex="0" aria-label="Main heading: Welcome to AccessMate" className="text-3xl font-bold">Welcome to AccessMate</h1>
        <p tabIndex="0" aria-label="Description text">
          This is a voice-assisted, keyboard-navigable platform designed to help blind users access digital content.
        </p>
        <button tabIndex="0" aria-label="Get Started button" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
          Get Started
        </button>
        <div class="container">
            <div class="heading">
                
New York Tech Google Learning Program
            </div>
        </div>
      </main>
    </div>
  );
}

export default App;
