import React from "react";

// ðŸ—£ï¸ Speak on hover
const speak = (text) => {
  if (window.speechSynthesis) {
    const utterance = new SpeechSynthesisUtterance(text);
    speechSynthesis.cancel(); // Stop any ongoing speech
    speechSynthesis.speak(utterance);
  }
};

const CourseCard = ({ title, subtitle, image, badge }) => {
  const handleHover = () => {
    const message = `${title}. ${subtitle}. Offered by Google.`;
    speak(message);
  };

  return (
    <div
      className="bg-white rounded-xl shadow-md p-4 w-full max-w-xs hover:shadow-lg transition-shadow duration-200"
      onMouseEnter={handleHover}
    >
      <div className="relative">
        <img
          src={image}
          alt={title}
          className="rounded-md mb-3 w-full h-40 object-cover"
        />
        {badge && (
          <span className="absolute top-2 right-2 bg-blue-100 text-blue-800 text-xs font-semibold px-2 py-1 rounded">
            {badge}
          </span>
        )}
      </div>

      <div className="text-left">
        <p className="text-sm text-gray-500">Google</p>
        <h3 className="text-lg font-semibold mb-1">{title}</h3>
        <p className="text-xs text-blue-700 underline">Build toward a degree</p>
        <p className="text-xs text-gray-500">{subtitle}</p>
      </div>
    </div>
  );
};

export default CourseCard;
